name def get_model ( self ) -> str : """Get model.""" return self . _device_model def get_manufacturer ( self ) -> str : """Get manufacturer.""" return self . _manufacturer def get_firmware ( self ) -> str : """Get firmware.""" return self . _firmware def is_online ( self ) -> bool : """Get online.""" return self . _online def get_all_sensors ( self ) -> list [ ImouEntity ]: """Get all the sensor instances.""" sensors = [] for ( platform , # pylint: disable=unused-variable sensor_instances_array , ) in self . _sensor_instances . items (): for sensor_instance in sensor_instances_array : sensors . append ( sensor_instance ) return sensors def get_sensors_by_platform ( self , platform : str ) -> list [ ImouEntity ]: """Get sensor instances associated to a given platform.""" if platform not in self . _sensor_instances : return [] return self . _sensor_instances [ platform ] def get_sensor_by_name ( self , name : str ) -> Union [ ImouSensor , ImouBinarySensor , ImouSwitch , None ]: """Get sensor instance with a given name.""" for ( platform , # pylint: disable=unused-variable sensor_instances_array , ) in self . _sensor_instances . items (): for sensor_instance in sensor_instances_array : if sensor_instance . get_name () == name : return sensor_instance return None def set_enabled ( self , value : bool ) -> None : """Set enable.""" self . _enabled = value def is_enabled ( self ) -> bool : """Is enabled.""" return self . _enabled async def async_initialize ( self ) -> None : """Initialize the instance by retrieving the device details and associated sensors.""" # get the details for this device from the API device_array = await self . _api_client . async_api_deviceBaseDetailList ([ self . _device_id ]) if "deviceList" not in device_array or len ( device_array [ "deviceList" ]) != 1 : raise InvalidResponse ( f "deviceList not found in { str ( device_array ) } " ) # reponse is an array, our data is in the first element device_data = device_array [ "deviceList" ][ 0 ] try : #