ies : list [ str ] = [] self . _switches : list [ str ] = [] self . _sensor_instances : dict [ str , list ] = { "switch" : [], "sensor" : [], "binary_sensor" : []} self . _last_alarm = "N.A." # setup the API client self . api_client = ImouAPIClient ( app_id , app_secret , websession , base_url , timeout ) # other status variables self . _initialized = False self . _connected = False self . _enabled = True def get_device_id ( self ) -> str : """Get device id.""" return self . _device_id def get_name ( self ) -> str : """Get device name.""" if self . _given_name != "" : return self . _given_name return self . _name def set_name ( self , given_name : str ) -> None : """Set device name.""" self . _given_name = given_name def get_model ( self ) -> str : """Get model.""" return self . _device_model def get_manufacturer ( self ) -> str : """Get manufacturer.""" return self . _manufacturer def get_firmware ( self ) -> str : """Get firmware.""" return self . _firmware def is_online ( self ) -> bool : """Get online.""" return self . _online def get_sensors ( self , platform : str ) -> list [ ImouEntity ]: """Get sensor instances.""" return self . _sensor_instances [ platform ] def set_enabled ( self , value : bool ) -> None : """Set enable.""" self . _enabled = value def get_last_alarm ( self ) -> str : """Get last alarm.""" return self . _last_alarm async def async_connect ( self ) -> bool : """Connect to the API.""" status = await self . api_client . async_connect () if status : self . _connected = True return status async def async_initialize ( self ) -> None : """Initialize the instance by retrieving the device details.""" # get the details for this device from the API device_array = await self . api_client . async_api_deviceBaseDetailList ([ self . _device_id ]) if "deviceList" not in device_array or len ( device_array [ "deviceList" ]) != 1 : raise InvalidResponse ( f "deviceList not found in { str ( device_array ) } " ) # reponse is an array, our data is in the first e